function getCurrency(t,c){var e=$(t).data("currency-cc"),r=parseFloat($(t).data("value").replace(/ /g,""));$.ajax({url:"/valutes/getcurcourse",data:{cc:e},method:"post",success:function(c){$(t).html(getCurrencyString(Math.round(c.Value*r,0))+' <i class="fa fa-rub"></i>'),$(t).tooltip("destroy")}})}function getCurrencyString(t){return t.toFixed(2).replace(/(\d)(?=(\d{3})+\.)/g,"$1 ")}$(function(){$("a[data-currency-cc]").each(function(){$(this).tooltip(),$(this).one("click",function(){getCurrency(this)})})});